import Map from './Map';
import React, { useState, useEffect } from 'react';
import RestaurantList from "./RestaurantList";
import LoginForm from './LoginForm';
import { createCustomPoint, getCustomPoints, deleteCustomPoint } from './api/customPointApi';
import { createRoute, getRoutes, deleteRoute } from './api/routeApi';

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    {
        name: "Eldon Square",
        location: { lat: 54.9747, lng: -1.6134 },
    },
    {
        name: "Tesco Express",
        location: { lat: 54.9775, lng: -1.6142 },
    },
    {
        name: "Grainger Market",
        location: { lat: 54.9728, lng: -1.6145 },
    },
    {
        name: "The Gate",
        location: { lat: 54.9719, lng: -1.6175 },
    },
];

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userId, setUserId] = useState(null);
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // Store custom points
    const [newPointName, setNewPointName] = useState(''); // Custom point name
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // Custom point location

    const [savedRoutes, setSavedRoutes] = useState([]); // 新增：保存路线状态
    const [routeName, setRouteName] = useState(''); // 新增：路线名称

    // 检查用户是否已登录
    useEffect(() => {
        const token = localStorage.getItem('token');
        const storedUserId = localStorage.getItem('userId');
        if (token && storedUserId) {
            setIsLoggedIn(true);
            setUserId(storedUserId);
        }
    }, []);

    // 加载用户的自定义点和路线
    useEffect(() => {
        const loadUserData = async () => {
            if (isLoggedIn && userId) {
                try {
                    // 加载自定义点
                    const pointsResponse = await getCustomPoints();
                    if (pointsResponse.success) {
                        setCustomPoints(pointsResponse.customPoints || []);
                    }

                    // 加载路线
                    const routesResponse = await getRoutes();
                    if (routesResponse.success) {
                        setSavedRoutes(routesResponse.routes || []);
                    }
                } catch (error) {
                    console.error('加载用户数据失败:', error);
                }
            }
        };

        loadUserData();
    }, [isLoggedIn, userId]);

    // 处理登录成功
    const handleLoginSuccess = (newUserId) => {
        setIsLoggedIn(true);
        setUserId(newUserId);
    };

    // 处理退出登录
    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        setIsLoggedIn(false);
        setUserId(null);
        // 清除用户数据
        setSelectedRestaurants([]);
        setCustomPoints([]);
        setSavedRoutes([]);
    };

    // 新增：保存当前路线
    const handleSaveRoute = async (e) => {
        e.preventDefault();
        if (routeName && selectedRestaurants.length > 0) {
            try {
                const newRoute = {
                    name: routeName,
                    locations: [...selectedRestaurants], // 复制当前选中的地点
                    createdAt: new Date().toISOString(),
                    userId: userId // 添加userId关联
                };

                const response = await createRoute(newRoute);

                if (response.success) {
                    console.log('路线保存成功:', response);
                    // 如果后端返回了带id的路线对象，使用它，否则使用本地创建的
                    const savedRoute = response.route || newRoute;
                    setSavedRoutes(prev => [...prev, savedRoute]);
                } else {
                    console.error('路线保存失败:', response.message);
                }

                setRouteName('');
            } catch (error) {
                console.error('保存路线出错:', error);
            }
        }
    };

    // 新增：加载保存的路线
    const loadSavedRoute = (route) => {
        setSelectedRestaurants(route.locations);
    };

    // Handle restaurant click event
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // Handle custom point form submission
    const handleAddCustomPoint = async (e) => {
        e.preventDefault();
        console.log('Form submitted'); // Log submission
        if (newPointName && newPointLocation.lat && newPointLocation.lng) {
            try {
                const newPoint = {
                    name: newPointName,
                    location: newPointLocation,
                    isCustom: true, // Mark as custom point
                    userId: userId // 添加userId关联
                };

                // 向后端发送请求创建自定义点
                const response = await createCustomPoint(newPoint);

                if (response.success) {
                    console.log('自定义点保存成功:', response);
                    // 如果后端返回了带id的点对象，使用它，否则使用本地创建的
                    const savedPoint = response.point || newPoint;
                    setCustomPoints(prev => [...prev, savedPoint]);
                } else {
                    console.error('自定义点保存失败:', response.message);
                }

                setNewPointName('');
                setNewPointLocation({ lat: null, lng: null });
            } catch (error) {
                console.error('保存自定义点出错:', error);
            }
        } else {
            console.error('Missing name or location'); // Log error
        }
    };

    // Handle map click event (to get custom point location)
    const handleMapClick = (lat, lng) => {
        console.log('Map click received:', lat, lng); // Log map click
        setNewPointLocation({ lat, lng });
    };

    // Merge predefined points and custom points
    const allPoints = [...restaurantList, ...customPoints];

    // Listen for changes in customPoints state
    useEffect(() => {
        console.log('Custom points updated:', customPoints); // Log custom points update
    }, [customPoints]);

    // 删除自定义点
    const handleDeleteCustomPoint = async (point) => {
        try {
            // 确保point有point_id属性
            if (!point.point_id) {
                console.error('无法删除点：缺少point_id');
                return;
            }

            const response = await deleteCustomPoint(point.point_id);

            if (response.success) {
                console.log('自定义点删除成功:', response);
                // 从本地状态中移除该点
                setCustomPoints(prevPoints => 
                    prevPoints.filter(p => p.point_id !== point.point_id)
                );
            } else {
                console.error('自定义点删除失败:', response.message);
            }
        } catch (error) {
            console.error('删除自定义点出错:', error);
        }
    };

    // 删除路线
    const handleDeleteRoute = async (route) => {
        try {
            // 确保route有route_id属性
            if (!route.route_id) {
                console.error('无法删除路线：缺少route_id');
                return;
            }

            const response = await deleteRoute(route.route_id);

            if (response.success) {
                console.log('路线删除成功:', response);
                // 从本地状态中移除该路线
                setSavedRoutes(prevRoutes => 
                    prevRoutes.filter(r => r.route_id !== route.route_id)
                );
            } else {
                console.error('路线删除失败:', response.message);
            }
        } catch (error) {
            console.error('删除路线出错:', error);
        }
    };

    // 如果用户未登录，显示登录表单
    if (!isLoggedIn) {
        return <LoginForm onLoginSuccess={handleLoginSuccess} />;
    }

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            {/* Left Panel */}
            <div style={{
                width: '300px',
                padding: '20px',
                backgroundColor: '#f5f5f5',
                boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)'
            }}>
                {/* 用户信息和登出按钮 */}
                <div style={{marginBottom: '20px', textAlign: 'center'}}>
                    <h3 style={{margin: '0 0 10px 0'}}>欢迎用户: {userId}</h3>
                    <button 
                        onClick={handleLogout}
                        style={{...styles.button, backgroundColor: '#dc3545'}}
                    >
                        退出登录
                    </button>
                </div>

                <form onSubmit={handleSaveRoute} style={{marginBottom: '20px'}}>
                    <h3 style={styles.heading}>保存当前路线</h3>
                    <input
                        type="text"
                        placeholder="路线名称"
                        value={routeName}
                        onChange={(e) => setRouteName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <button
                        type="submit"
                        style={styles.button}
                    >
                        保存路线
                    </button>
                </form>
                {/* Add Custom Point Form */}
                <form onSubmit={handleAddCustomPoint} style={{marginBottom: '20px'}}>
                    <h3 style={styles.heading}>添加自定义点</h3>
                    <input
                        type="text"
                        placeholder="输入自定义点名称"
                        value={newPointName}
                        onChange={(e) => setNewPointName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <div style={{marginTop: '10px'}}>
                        <span style={styles.label}>点击地图选择位置</span>
                        <div style={styles.locationDisplay}>
                            {newPointLocation.lat && newPointLocation.lng
                                ? `已选位置: ${newPointLocation.lat.toFixed(4)}, ${newPointLocation.lng.toFixed(4)}`
                                : '未选择位置'}
                        </div>
                    </div>
                    <button
                        type="submit"
                        style={styles.button}
                    >
                        添加自定义点
                    </button>
                </form>

                {/* Clear Selection Button */}
                <button
                    onClick={() => setSelectedRestaurants([])}
                    style={styles.button}
                >
                    清除选择
                </button>

                {/* Display All Points (including custom points) */}
                <RestaurantList
                    list={allPoints}
                    selectedLocations={selectedRestaurants}
                    onClickHandler={handleRestaurantClick}
                    onDeleteCustomPoint={handleDeleteCustomPoint}
                />
            </div>

            {/* Map Component */}
            <div style={{flex: 1}}>
                <Map
                    apikey={apikey}
                    userPosition={userPosition}
                    selectedLocations={selectedRestaurants}
                    onMapClick={handleMapClick} // Pass map click event handler
                    customPoints={customPoints} // Pass custom points
                    restaurantList={restaurantList}
                />
            </div>

            {/* 保存的路线列表 */}
            <div style={{ width: '250px', padding: '20px', overflowY: 'auto' }}>
                <h3 style={styles.heading}>已保存路线</h3>
                <div style={styles.savedRoutesList}>
                    {savedRoutes.length === 0 ? (
                        <div style={styles.emptyMessage}>暂无保存的路线</div>
                    ) : (
                        savedRoutes.map(route => (
                            <div
                                key={route.route_id || route.createdAt}
                                style={styles.routeItem}
                            >
                                <div 
                                    style={styles.routeInfo}
                                    onClick={() => loadSavedRoute(route)}
                                >
                                    <div>{route.name}</div>
                                    <small style={styles.dateText}>
                                        {new Date(route.createdAt).toLocaleDateString()}
                                    </small>
                                </div>
                                <button
                                    style={styles.deleteButton}
                                    onClick={() => handleDeleteRoute(route)}
                                    title="删除此路线"
                                >
                                    删除
                                </button>
                            </div>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
}

