import React, { useState, useEffect } from 'react';
import Map from './Map';
import RestaurantList from './RestaurantList';
import Auth from './components/Auth/Auth'; // 假设Auth.js放在components/Auth目录下
import axios from 'axios';

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    {
        name: "Eldon Square",
        location: { lat: 54.9747, lng: -1.6134 },
    },
    {
        name: "Tesco Express",
        location: { lat: 54.9775, lng: -1.6142 },
    },
    {
        name: "Grainger Market",
        location: { lat: 54.9728, lng: -1.6145 },
    },
    {
        name: "The Gate",
        location: { lat: 54.9719, lng: -1.6175 },
    },
];

function App() {
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // 存储用户自定义点
    const [newPointName, setNewPointName] = useState(''); // 自定义点名称
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // 自定义点位置
    const [user, setUser] = useState(null); // 用户登录状态

    // 检查用户是否已登录
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axios.get('/api/auth/me', {
                headers: { Authorization: `Bearer ${token}` }
            })
                .then(res => {
                    setUser(res.data);
                    loadUserPoints(res.data.id); // 加载用户的点
                })
                .catch(() => {
                    localStorage.removeItem('token');
                });
        }
    }, []);

    // 加载用户的点
    const loadUserPoints = async (userId) => {
        try {
            const { data } = await axios.get(`/api/points?userId=${userId}`, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setCustomPoints(data);
        } catch (error) {
            console.error('Error loading points:', error);
        }
    };

    // 处理餐厅点击事件
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // 处理添加自定义点
    const handleAddCustomPoint = async (e) => {
        e.preventDefault();
        if (!user) return alert('Please login first');
        if (!newPointName || !newPointLocation.lat || !newPointLocation.lng) {
            return alert('Please provide a name and select a location on the map.');
        }

        try {
            const { data } = await axios.post('/api/points', {
                name: newPointName,
                lat: newPointLocation.lat,
                lng: newPointLocation.lng,
                userId: user.id
            }, {
                headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
            });
            setCustomPoints(prev => [...prev, data]);
            setNewPointName('');
            setNewPointLocation({ lat: null, lng: null });
        } catch (error) {
            console.error('Error saving point:', error);
        }
    };

    // 处理地图点击事件
    const handleMapClick = (lat, lng) => {
        setNewPointLocation({ lat, lng });
    };

    // 合并预定义点和自定义点
    const allPoints = [...restaurantList, ...customPoints];

    // 监听customPoints的变化
    useEffect(() => {
        console.log('Custom points updated:', customPoints);
    }, [customPoints]);

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            {!user ? (
                <Auth onLogin={(userData) => {
                    setUser(userData);
                    loadUserPoints(userData.id);
                }} />
            ) : (
                <>
                    {/* 用户信息栏 */}
                    <div style={{ position: 'absolute', top: '10px', right: '10px', zIndex: 1000 }}>
                        Welcome, {user.email}!
                        <button
                            onClick={() => {
                                localStorage.removeItem('token');
                                setUser(null);
                                setCustomPoints([]);
                            }}
                            style={{ marginLeft: '10px' }}
                        >
                            Logout
                        </button>
                    </div>

                    {/* 左侧面板 */}
                    <div style={{ width: '300px', padding: '20px', backgroundColor: '#f5f5f5', boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)' }}>
                        {/* 添加自定义点表单 */}
                        <form onSubmit={handleAddCustomPoint} style={{ marginBottom: '20px' }}>
                            <h3 style={styles.heading}>Add Custom Point</h3>
                            <input
                                type="text"
                                placeholder="Enter custom point name"
                                value={newPointName}
                                onChange={(e) => setNewPointName(e.target.value)}
                                style={styles.input}
                                required
                            />
                            <div style={{ marginTop: '10px' }}>
                                <span style={styles.label}>Click on the map to select location</span>
                                <div style={styles.locationDisplay}>
                                    {newPointLocation.lat && newPointLocation.lng
                                        ? `Selected Location: ${newPointLocation.lat.toFixed(4)}, ${newPointLocation.lng.toFixed(4)}`
                                        : 'No location selected'}
                                </div>
                            </div>
                            <button type="submit" style={styles.button}>
                                Add Custom Point
                            </button>
                        </form>

                        {/* 清除选择按钮 */}
                        <button
                            onClick={() => setSelectedRestaurants([])}
                            style={styles.button}
                        >
                            Clear Selection
                        </button>

                        {/* 显示所有点 */}
                        <RestaurantList
                            list={allPoints}
                            selectedLocations={selectedRestaurants}
                            onClickHandler={handleRestaurantClick}
                        />
                    </div>

                    {/* 地图组件 */}
                    <div style={{ flex: 1 }}>
                        <Map
                            apikey={apikey}
                            userPosition={userPosition}
                            selectedLocations={selectedRestaurants}
                            onMapClick={handleMapClick}
                            customPoints={customPoints}
                            restaurantList={restaurantList}
                        />
                    </div>
                </>
            )}
        </div>
    );
}

// CSS样式
const styles = {
    button: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
        marginBottom: '20px',
        transition: 'background-color 0.3s ease',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        fontSize: '16px',
    },
    heading: {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#333',
    },
    label: {
        fontSize: '14px',
        color: '#666',
    },
    locationDisplay: {
        padding: '10px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '5px',
        marginTop: '5px',
        fontSize: '14px',
        color: '#333',
    },
};

export default App;
