import React from 'react';

// Restaurant physical components
function RestaurantEntity(props) {
    const { data, isSelected, onClickHandler, onDeleteHandler } = props;

    // Processing click event
    const handleClick = () => {
        onClickHandler(data.location);
    };

    // 处理删除点击事件
    const handleDelete = (e) => {
        e.stopPropagation(); // 阻止事件冒泡，避免触发选择事件
        if (onDeleteHandler) {
            onDeleteHandler(data);
        }
    };

    // Style of restaurant entry
    const entryStyle = {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "10px",
        margin: "5px",
        border: "1px solid #ddd",
        borderRadius: "5px",
        cursor: "pointer",
        backgroundColor: isSelected ? '#e3f2fd' : 'white', // the color when choose
        transition: 'background-color 0.3s ease', // Add transition effect
    };

    const deleteButtonStyle = {
        backgroundColor: '#dc3545',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        padding: '2px 6px',
        cursor: 'pointer',
        fontSize: '12px',
        marginLeft: '8px',
    };

    return (
        <div style={entryStyle} onClick={handleClick}>
            <div>
                {data.name} {isSelected && '✓'} {/* A checkmark is displayed when selected */}
            </div>
            {data.isCustom && (
                <button 
                    style={deleteButtonStyle} 
                    onClick={handleDelete}
                    title="删除此自定义点"
                >
                    删除
                </button>
            )}
        </div>
    );
}

function RestaurantList(props) {
    const { list, selectedLocations, onClickHandler, onDeleteCustomPoint } = props;

    const restaurantEntries = list.map((entry) => {
        const isSelected = selectedLocations.some(
            (location) =>
                location.lat === entry.location.lat &&
                location.lng === entry.location.lng
        );

        return (
            <RestaurantEntity
                key={entry.isCustom ? entry.point_id || entry.name : entry.name} // 使用point_id作为自定义点的key
                data={entry}
                isSelected={isSelected}
                onClickHandler={onClickHandler}
                onDeleteHandler={entry.isCustom ? onDeleteCustomPoint : null}
            />
        );
    });

    // Restaurant list container style
    const listStyle = {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px', // Spacing between entries
        padding: '10px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        backgroundColor: '#f9f9f9',
        maxHeight: '300px',
        overflowY: 'auto',
    };

    return (
        <div id="restaurant-list" style={listStyle}>
            {restaurantEntries}
        </div>
    );
}

export default RestaurantList;
