import React, { useState } from 'react';
import Map from './Map';
import RestaurantList from './RestaurantList';

function App() {
    const apikey = 'YOUR_HERE_MAPS_API_KEY'; // 替换为你的 Here Maps API Key

    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [savedRoutes, setSavedRoutes] = useState([]); // 存储已保存的路线
    const [selectedRoute, setSelectedRoute] = useState(null); // 当前选中的路线
    const [routeName, setRouteName] = useState(''); // 存储路线名称

    // 预设的餐厅数据
    const restaurantList = [
        { name: "Restaurant A", location: { lat: 37.7749, lng: -122.4194 } },
        { name: "Restaurant B", location: { lat: 37.7849, lng: -122.4094 } },
        { name: "Restaurant C", location: { lat: 37.7649, lng: -122.4294 } }
    ];

    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(p => p.lat === location.lat && p.lng === location.lng);
            return exists ? prev.filter(p => p.lat !== location.lat || p.lng !== location.lng) : [...prev, location];
        });
    };

    const handleSaveRoute = () => {
        if (routeName && selectedRestaurants.length > 1) {
            const newRoute = { name: routeName, locations: [...selectedRestaurants] };
            setSavedRoutes(prev => [...prev, newRoute]);
            setRouteName('');
        } else {
            alert('请输入路线名称，并至少选择两个地点');
        }
    };

    const handleRouteClick = (route) => {
        setSelectedRoute(route);
        setSelectedRestaurants(route.locations);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* 左侧面板 */}
            <div style={{ width: "300px", padding: "10px", borderRight: "1px solid #ddd", background: "#f7f7f7" }}>
                <h3>餐厅列表</h3>
                <RestaurantList list={restaurantList} selectedLocations={selectedRestaurants} onClickHandler={handleRestaurantClick} />

                <h3>保存路线</h3>
                <input
                    type="text"
                    placeholder="输入路线名称"
                    value={routeName}
                    onChange={(e) => setRouteName(e.target.value)}
                    style={{ width: "100%", padding: "5px", marginBottom: "5px" }}
                />
                <button onClick={handleSaveRoute} style={{ width: "100%", padding: "5px", background: "#007bff", color: "white", border: "none" }}>
                    保存路线
                </button>

                <h3>已保存的路线</h3>
                <ul>
                    {savedRoutes.map((route, index) => (
                        <li key={index} style={{ cursor: 'pointer', color: 'blue' }} onClick={() => handleRouteClick(route)}>
                            {route.name}
                        </li>
                    ))}
                </ul>
            </div>

            {/* 右侧地图 */}
            <div style={{ flex: 1 }}>
                <Map
                    apikey={apikey}
                    selectedLocations={selectedRestaurants}
                    restaurantList={restaurantList}
                    selectedRoute={selectedRoute}
                />
            </div>
        </div>
    );
}

export default App;
