import Map from './Map';
import React, { useState } from 'react';
import RestaurantList from "./RestaurantList";

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    {
        name: "Eldon Square",
        location: { lat: 54.9747, lng: -1.6134 },
    },
    {
        name: "Tesco Express",
        location: { lat: 54.9775, lng: -1.6142 },
    },
    {
        name: "Grainger Market",
        location: { lat: 54.9728, lng: -1.6145 },
    },
    {
        name: "The Gate",
        location: { lat: 54.9719, lng: -1.6175 },
    },
];

function App() {
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // 存储自定义点
    const [newPointName, setNewPointName] = useState(''); // 自定义点名称
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // 自定义点位置

    // 处理餐厅点击事件
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // 处理自定义点表单提交
    const handleAddCustomPoint = (e) => {
        e.preventDefault();
        if (newPointName && newPointLocation.lat && newPointLocation.lng) {
            const newPoint = {
                name: newPointName,
                location: newPointLocation,
                isCustom: true, // 标记为自定义点
            };
            setCustomPoints(prev => [...prev, newPoint]);
            setNewPointName('');
            setNewPointLocation({ lat: null, lng: null });
        }
    };

    // 处理地图点击事件（获取自定义点的位置）
    const handleMapClick = (lat, lng) => {
        setNewPointLocation({ lat, lng });
    };

    // 合并事先定义好的点和自定义点
    const allPoints = [...restaurantList, ...customPoints];

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ marginRight: '20px' }}>
                <button onClick={() => setSelectedRestaurants([])}>
                    清除选择
                </button>
                {/* 自定义点输入表单 */}
                <form onSubmit={handleAddCustomPoint} style={{ marginBottom: '20px' }}>
                    <input
                        type="text"
                        placeholder="输入自定义点名称"
                        value={newPointName}
                        onChange={(e) => setNewPointName(e.target.value)}
                        required
                    />
                    <div style={{ marginTop: '10px' }}>
                        <span>点击地图选择位置</span>
                        <div>
                            {newPointLocation.lat && newPointLocation.lng
                                ? `已选择位置: ${newPointLocation.lat}, ${newPointLocation.lng}`
                                : '未选择位置'}
                        </div>
                    </div>
                    <button type="submit" style={{ marginTop: '10px' }}>添加自定义点</button>
                </form>
                {/* 显示所有点（包括自定义点） */}
                <RestaurantList
                    list={allPoints}
                    selectedLocations={selectedRestaurants}
                    onClickHandler={handleRestaurantClick}
                />
            </div>
            {/* 地图组件 */}
            <Map
                apikey={apikey}
                userPosition={userPosition}
                selectedLocations={selectedRestaurants}
                onMapClick={handleMapClick} // 传递地图点击事件处理函数
                customPoints={customPoints} // 传递自定义点
            />
        </div>
    );
}

export default App;
