import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = ({ onLoginSuccess }) => {
    const [isLogin, setIsLogin] = useState(true);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErrorMessage('');
        setIsLoading(true);
        
        try {
            // 简化请求URL，确保使用正确的地址
            const endpoint = isLogin ? 'http://localhost:5000/api/login' : 'http://localhost:5000/api/register';
            
            console.log('发送请求到:', endpoint);
            const response = await axios.post(endpoint, { username, password });
            
            console.log('服务器响应:', response.data);
            
            if (response.data.success) {
                // 存储登录信息
                localStorage.setItem('token', response.data.token);
                localStorage.setItem('userId', response.data.userId);
                
                // 通知父组件登录成功
                onLoginSuccess(response.data.userId);
            } else {
                setErrorMessage(response.data.message || '操作失败');
            }
        } catch (error) {
            console.error('认证失败:', error);
            
            // 更详细的错误处理
            if (error.response) {
                // 服务器返回了错误状态码
                console.log('错误响应:', error.response.data);
                setErrorMessage(error.response.data?.message || `服务器错误 (${error.response.status})`);
            } else if (error.request) {
                // 请求发出但没有收到响应
                console.log('请求错误:', error.request);
                setErrorMessage('无法连接到服务器，请检查后端服务是否运行');
            } else {
                // 请求设置时出错
                setErrorMessage('请求错误: ' + error.message);
            }
        } finally {
            setIsLoading(false);
        }
    };

    const toggleMode = () => {
        setIsLogin(!isLogin);
        setErrorMessage('');
    };

    return (
        <div style={styles.container}>
            <div style={styles.formCard}>
                <h2 style={styles.title}>{isLogin ? '用户登录' : '用户注册'}</h2>
                
                {errorMessage && <div style={styles.errorMessage}>{errorMessage}</div>}
                
                <form onSubmit={handleSubmit}>
                    <div style={styles.inputGroup}>
                        <label style={styles.label}>用户名</label>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            style={styles.input}
                            required
                        />
                    </div>
                    
                    <div style={styles.inputGroup}>
                        <label style={styles.label}>密码</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            style={styles.input}
                            required
                        />
                    </div>
                    
                    <button 
                        type="submit" 
                        style={styles.button}
                        disabled={isLoading}
                    >
                        {isLoading ? '处理中...' : (isLogin ? '登录' : '注册')}
                    </button>
                </form>
                
                <div style={styles.switchMode}>
                    <span>{isLogin ? '没有账号？' : '已有账号？'}</span>
                    <button 
                        onClick={toggleMode} 
                        style={styles.switchButton}
                        disabled={isLoading}
                    >
                        {isLogin ? '注册' : '登录'}
                    </button>
                </div>
                
                <div style={styles.helpText}>
                    提示: 随便输入用户名和密码，如果是新用户会自动注册
                </div>
            </div>
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f5f5f5',
    },
    formCard: {
        backgroundColor: 'white',
        borderRadius: '8px',
        padding: '40px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        width: '350px',
    },
    title: {
        textAlign: 'center',
        marginBottom: '24px',
        color: '#333',
        fontSize: '24px',
    },
    inputGroup: {
        marginBottom: '20px',
    },
    label: {
        display: 'block',
        marginBottom: '8px',
        fontSize: '14px',
        color: '#555',
    },
    input: {
        width: '100%',
        padding: '10px',
        borderRadius: '4px',
        border: '1px solid #ddd',
        fontSize: '16px',
    },
    button: {
        width: '100%',
        padding: '12px',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        fontSize: '16px',
        cursor: 'pointer',
        marginTop: '10px',
    },
    errorMessage: {
        backgroundColor: '#ffebee',
        color: '#f44336',
        padding: '10px',
        borderRadius: '4px',
        marginBottom: '16px',
        fontSize: '14px',
    },
    switchMode: {
        marginTop: '16px',
        textAlign: 'center',
        fontSize: '14px',
        color: '#666',
    },
    switchButton: {
        backgroundColor: 'transparent',
        border: 'none',
        color: '#007bff',
        cursor: 'pointer',
        fontSize: '14px',
        marginLeft: '4px',
    },
    helpText: {
        marginTop: '20px',
        textAlign: 'center',
        fontSize: '13px',
        color: '#888',
        fontStyle: 'italic'
    }
};

export default LoginForm;
