import Map from './Map';
import React, { useState, useEffect } from 'react';
import RestaurantList from "./RestaurantList";

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    {
        name: "Eldon Square",
        location: { lat: 54.9747, lng: -1.6134 },
    },
    {
        name: "Tesco Express",
        location: { lat: 54.9775, lng: -1.6142 },
    },
    {
        name: "Grainger Market",
        location: { lat: 54.9728, lng: -1.6145 },
    },
    {
        name: "The Gate",
        location: { lat: 54.9719, lng: -1.6175 },
    },
];

function App() {
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // 存储自定义点
    const [newPointName, setNewPointName] = useState(''); // 自定义点名称
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // 自定义点位置

    // 处理餐厅点击事件
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // 处理自定义点表单提交
    const handleAddCustomPoint = (e) => {
        e.preventDefault();
        console.log('Form submitted'); // 添加日志
        if (newPointName && newPointLocation.lat && newPointLocation.lng) {
            const newPoint = {
                name: newPointName,
                location: newPointLocation,
                isCustom: true, // 标记为自定义点
            };
            setCustomPoints(prev => [...prev, newPoint]);
            setNewPointName('');
            setNewPointLocation({ lat: null, lng: null });
        } else {
            console.error('Missing name or location'); // 添加错误日志
        }
    };

    // 处理地图点击事件（获取自定义点的位置）
    const handleMapClick = (lat, lng) => {
        console.log('Map click received:', lat, lng); // 添加日志
        setNewPointLocation({ lat, lng });
    };

    // 合并事先定义好的点和自定义点
    const allPoints = [...restaurantList, ...customPoints];

    // 监听 customPoints 状态变化
    useEffect(() => {
        console.log('Custom points updated:', customPoints); // 添加日志
    }, [customPoints]);

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            {/* 左侧面板 */}
            <div style={{ width: '300px', padding: '20px', backgroundColor: '#f5f5f5', boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)' }}>
                {/* 清除选择按钮 */}
                <button
                    onClick={() => setSelectedRestaurants([])}
                    style={styles.button}
                >
                    清除选择
                </button>

                {/* 自定义点输入表单 */}
                <form onSubmit={handleAddCustomPoint} style={{ marginBottom: '20px' }}>
                    <h3 style={styles.heading}>添加自定义点</h3>
                    <input
                        type="text"
                        placeholder="输入自定义点名称"
                        value={newPointName}
                        onChange={(e) => setNewPointName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <div style={{ marginTop: '10px' }}>
                        <span style={styles.label}>点击地图选择位置</span>
                        <div style={styles.locationDisplay}>
                            {newPointLocation.lat && newPointLocation.lng
                                ? `已选择位置: ${newPointLocation.lat.toFixed(4)}, ${newPointLocation.lng.toFixed(4)}`
                                : '未选择位置'}
                        </div>
                    </div>
                    <button
                        type="submit"
                        style={styles.button}
                    >
                        添加自定义点
                    </button>
                </form>

                {/* 显示所有点（包括自定义点） */}
                <RestaurantList
                    list={allPoints}
                    selectedLocations={selectedRestaurants}
                    onClickHandler={handleRestaurantClick}
                />
            </div>

            {/* 地图组件 */}
            <div style={{ flex: 1 }}>
                <Map
                    apikey={apikey}
                    userPosition={userPosition}
                    selectedLocations={selectedRestaurants}
                    onMapClick={handleMapClick} // 传递地图点击事件处理函数
                    customPoints={customPoints} // 传递自定义点
                />
            </div>
        </div>
    );
}

// CSS 样式
const styles = {
    button: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
        marginBottom: '20px',
        transition: 'background-color 0.3s ease',
    },
    buttonHover: {
        backgroundColor: '#0056b3',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        fontSize: '16px',
    },
    heading: {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#333',
    },
    label: {
        fontSize: '14px',
        color: '#666',
    },
    locationDisplay: {
        padding: '10px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '5px',
        marginTop: '5px',
        fontSize: '14px',
        color: '#333',
    },
};

export default App;
