import Map from './Map';
import React, { useState, useEffect } from 'react';
import RestaurantList from "./RestaurantList";

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    { name: "Eldon Square", location: { lat: 54.9747, lng: -1.6134 } },
    { name: "Tesco Express", location: { lat: 54.9775, lng: -1.6142 } },
    { name: "Grainger Market", location: { lat: 54.9728, lng: -1.6145 } },
    { name: "The Gate", location: { lat: 54.9719, lng: -1.6175 } },
];

function App() {
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // 自定义点列表
    const [newPointName, setNewPointName] = useState(''); // 自定义点名称
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // 自定义点坐标
    const [routeData, setRouteData] = useState(null); // 保存计算得到的路线数据
    const [routeName, setRouteName] = useState(''); // 自定义路线名称

    // 处理餐厅或已有点点击
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // 自定义点表单提交：保存点并传送到后端
    const handleAddCustomPoint = async (e) => {
        e.preventDefault();
        console.log('Custom point form submitted');
        if (newPointName && newPointLocation.lat && newPointLocation.lng) {
            const newPoint = {
                name: newPointName,
                location: newPointLocation,
                isCustom: true,
            };
            setCustomPoints(prev => [...prev, newPoint]);

            // 将自定义点数据传给后端
            try {
                const response = await fetch("/save_point", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({
                        x: newPointLocation.lat.toString(),
                        y: newPointLocation.lng.toString(),
                        name: newPointName
                    })
                });
                const data = await response.json();
                console.log("Saved point response:", data);
            } catch (error) {
                console.error("Error saving point:", error);
            }

            setNewPointName('');
            setNewPointLocation({ lat: null, lng: null });
        } else {
            console.error('Missing name or location');
        }
    };

    // 处理地图点击事件，接收点坐标
    const handleMapClick = (lat, lng) => {
        console.log('Map click received:', lat, lng);
        setNewPointLocation({ lat, lng });
    };

    // 回调：接收 Map 组件计算后的路线信息
    const handleRouteCalculated = (data) => {
        console.log("Route calculated:", data);
        setRouteData(data);
    };

    // 保存自定义路线：发送路线数据及自定义名称到后端
    const handleSaveRoute = async () => {
        if (!routeData || !routeName) {
            console.error("Route data or route name is missing");
            return;
        }
        const routeInfo = {
            name: routeName,
            polyline: routeData.polyline, // 此处只发送 polyline，可根据需要扩展其他字段
            // 可添加 bounds, start, waypoints 等字段
        };
        try {
            // 请确保在 package.json 中配置 proxy 或使用正确的端口地址
            const response = await fetch("/save_route", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(routeInfo)
            });
            const data = await response.json();
            console.log("Saved route response:", data);
        } catch (error) {
            console.error("Error saving route:", error);
        }
    };

    // 合并预定义点和自定义点
    const allPoints = [...restaurantList, ...customPoints];

    useEffect(() => {
        console.log('Custom points updated:', customPoints);
    }, [customPoints]);

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            {/* 左侧面板 */}
            <div style={{ width: '300px', padding: '20px', backgroundColor: '#f5f5f5', boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)' }}>
                {/* 自定义点表单 */}
                <form onSubmit={handleAddCustomPoint} style={{ marginBottom: '20px' }}>
                    <h3 style={styles.heading}>Add Custom Point</h3>
                    <input
                        type="text"
                        placeholder="Enter custom point name"
                        value={newPointName}
                        onChange={(e) => setNewPointName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <div style={{ marginTop: '10px' }}>
                        <span style={styles.label}>Click on the map to select location</span>
                        <div style={styles.locationDisplay}>
                            {newPointLocation.lat && newPointLocation.lng
                                ? `Selected Location: ${newPointLocation.lat.toFixed(4)}, ${newPointLocation.lng.toFixed(4)}`
                                : 'No location selected'}
                        </div>
                    </div>
                    <button type="submit" style={styles.button}>Add Custom Point</button>
                </form>

                {/* 清空选择 */}
                <button onClick={() => setSelectedRestaurants([])} style={styles.button}>Clear Selection</button>

                {/* 显示所有点 */}
                <RestaurantList
                    list={allPoints}
                    selectedLocations={selectedRestaurants}
                    onClickHandler={handleRestaurantClick}
                />

                {/* 自定义路线保存部分 */}
                <div style={{ marginTop: '20px' }}>
                    <h3 style={styles.heading}>Save Custom Route</h3>
                    <input
                        type="text"
                        placeholder="Enter route name"
                        value={routeName}
                        onChange={(e) => setRouteName(e.target.value)}
                        style={styles.input}
                    />
                    <button onClick={handleSaveRoute} style={styles.button}>Save Route</button>
                </div>
            </div>

            {/* 地图组件 */}
            <div style={{ flex: 1 }}>
                <Map
                    apikey={apikey}
                    userPosition={userPosition}
                    selectedLocations={selectedRestaurants}
                    onMapClick={handleMapClick}
                    customPoints={customPoints}
                    restaurantList={restaurantList}
                    onRouteCalculated={handleRouteCalculated}  // 传入路线计算回调
                />
            </div>
        </div>
    );
}

const styles = {
    button: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
        marginBottom: '20px',
        transition: 'background-color 0.3s ease',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        fontSize: '16px',
    },
    heading: {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#333',
    },
    label: {
        fontSize: '14px',
        color: '#666',
    },
    locationDisplay: {
        padding: '10px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '5px',
        marginTop: '5px',
        fontSize: '14px',
        color: '#333',
    },
};

export default App;
