import React from 'react';

// Restaurant physical components
const RestaurantEntity = ({ data, isSelected, onClickHandler, onDeleteHandler }) => {
    const handleClick = () => {
        onClickHandler(data);
    };

    const handleDelete = (e) => {
        e.stopPropagation(); // 阻止事件冒泡到父元素
        onDeleteHandler(data);
    };

    return (
        <div
            style={{
                ...styles.restaurantEntry,
                backgroundColor: isSelected ? '#e8f4fc' : '#fff',
                borderLeft: isSelected ? '4px solid #007bff' : '4px solid transparent',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}
            onClick={handleClick}
        >
            <div>
                <div style={styles.restaurantName}>
                    {data.name}
                    {data.isCustom && <span style={styles.customTag}>自定义</span>}
                </div>
                {data.address && (
                    <div style={styles.restaurantAddress}>{data.address}</div>
                )}
            </div>
            {data.isCustom && onDeleteHandler && (
                <button 
                    style={styles.deleteButton}
                    onClick={handleDelete}
                    title="删除此自定义点"
                >
                    删除
                </button>
            )}
        </div>
    );
};

const RestaurantList = ({ list, selectedLocations, onClickHandler, onDeleteCustomPoint }) => {
    // 函数检查当前餐厅是否已被选中
    const isRestaurantSelected = (restaurant) => {
        return selectedLocations.some(location => {
            // 处理不同的数据格式
            if (restaurant.isCustom) {
                return location.id === restaurant.id || location.point_id === restaurant.point_id;
            }
            return location.id === restaurant.id;
        });
    };

    return (
        <div style={styles.restaurantListContainer}>
            <h3 style={styles.heading}>餐厅列表</h3>
            <div style={styles.restaurantList}>
                {list.length === 0 ? (
                    <div style={styles.emptyMessage}>暂无餐厅数据</div>
                ) : (
                    list.map((restaurant) => (
                        <RestaurantEntity
                            key={restaurant.id || restaurant.point_id}
                            data={restaurant}
                            isSelected={isRestaurantSelected(restaurant)}
                            onClickHandler={onClickHandler}
                            onDeleteHandler={restaurant.isCustom ? onDeleteCustomPoint : null}
                        />
                    ))
                )}
            </div>
        </div>
    );
};

// CSS Styles
const styles = {
    restaurantListContainer: {
        marginBottom: '20px',
    },
    heading: {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#333',
    },
    restaurantList: {
        border: '1px solid #ddd',
        borderRadius: '5px',
        backgroundColor: '#fff',
        maxHeight: '400px',
        overflowY: 'auto',
    },
    restaurantEntry: {
        padding: '10px 15px',
        cursor: 'pointer',
        borderBottom: '1px solid #eee',
        transition: 'background-color 0.2s',
    },
    restaurantName: {
        fontWeight: '500',
        marginBottom: '4px',
        display: 'flex',
        alignItems: 'center',
    },
    restaurantAddress: {
        fontSize: '12px',
        color: '#666',
    },
    customTag: {
        fontSize: '10px',
        backgroundColor: '#28a745',
        color: 'white',
        padding: '2px 6px',
        borderRadius: '10px',
        marginLeft: '8px',
    },
    deleteButton: {
        backgroundColor: '#dc3545',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
        padding: '4px 8px',
        cursor: 'pointer',
        fontSize: '12px',
    },
    emptyMessage: {
        padding: '15px',
        textAlign: 'center',
        color: '#666',
    }
};

export default RestaurantList;
