import Map from './Map';
import React, { useState, useEffect } from 'react';
import RestaurantList from "./RestaurantList";

const apikey = '8kY020yd2oSy4ivQKBlxf_a5Bhtizzu0A9deSUakGz8';

const userPosition = { lat: 54.9783, lng: -1.6174 }; // Newcastle
const restaurantList = [
    { name: "Eldon Square", location: { lat: 54.9747, lng: -1.6134 } },
    { name: "Tesco Express", location: { lat: 54.9775, lng: -1.6142 } },
    { name: "Grainger Market", location: { lat: 54.9728, lng: -1.6145 } },
    { name: "The Gate", location: { lat: 54.9719, lng: -1.6175 } },
];

function App() {
    const [selectedRestaurants, setSelectedRestaurants] = useState([]);
    const [customPoints, setCustomPoints] = useState([]); // 自定义标点
    const [newPointName, setNewPointName] = useState(''); // 标点名称
    const [newPointLocation, setNewPointLocation] = useState({ lat: null, lng: null }); // 标点位置

    // 新增：用于保存路线的状态
    const [savedRoutes, setSavedRoutes] = useState([]);
    const [newRouteName, setNewRouteName] = useState('');
    const [activeRoute, setActiveRoute] = useState(null);
    // 假设 currentRoute 为自动计算的路线（例如一组坐标点），这里仅作示例
    const [currentRoute, setCurrentRoute] = useState(null);

    // 处理餐厅点击事件（选中/取消选中）
    const handleRestaurantClick = (location) => {
        setSelectedRestaurants(prev => {
            const exists = prev.some(l => l.lat === location.lat && l.lng === location.lng);
            return exists
                ? prev.filter(l => !(l.lat === location.lat && l.lng === location.lng))
                : [...prev, location];
        });
    };

    // 处理自定义标点的表单提交
    const handleAddCustomPoint = (e) => {
        e.preventDefault();
        console.log('Form submitted');
        if (newPointName && newPointLocation.lat && newPointLocation.lng) {
            const newPoint = {
                name: newPointName,
                location: newPointLocation,
                isCustom: true,
            };
            setCustomPoints(prev => [...prev, newPoint]);
            setNewPointName('');
            setNewPointLocation({ lat: null, lng: null });
        } else {
            console.error('Missing name or location');
        }
    };

    // 处理地图点击（获取自定义标点位置）
    const handleMapClick = (lat, lng) => {
        console.log('Map click received:', lat, lng);
        setNewPointLocation({ lat, lng });
    };

    // 示例：模拟自动计算路线逻辑，通常你可能会根据 selectedRestaurants 或 customPoints 来计算路线
    useEffect(() => {
        // 这里可以调用路线计算 API 或者逻辑，计算后更新 currentRoute
        // 举例：假设 currentRoute 是选中标点按顺序连线得到的路径
        if (selectedRestaurants.length > 1) {
            // 简单示例：直接将选中的餐厅位置连线
            setCurrentRoute(selectedRestaurants);
        } else {
            setCurrentRoute(null);
        }
    }, [selectedRestaurants]);

    // 处理保存路线的表单提交
    const handleSaveRoute = (e) => {
        e.preventDefault();
        if (newRouteName && currentRoute) {
            const newSavedRoute = {
                name: newRouteName,
                route: currentRoute, // 路线数据
            };
            setSavedRoutes(prev => [...prev, newSavedRoute]);
            setNewRouteName('');
            console.log('Route saved:', newRouteName, currentRoute);
        } else {
            console.error('Missing route name or current route is not available');
        }
    };

    const handleRouteClick = (route) => {
        setSelectedRoute(route);
        setSelectedRestaurants(route.locations); // 选中路线中的所有点
    };

    // 合并预设的标点和自定义标点
    const allPoints = [...restaurantList, ...customPoints];

    useEffect(() => {
        console.log('Custom points updated:', customPoints);
    }, [customPoints]);

    return (
        <div style={{ display: 'flex', height: '100vh' }}>
            {/* 左侧面板 */}
            <div style={{ width: '300px', padding: '20px', backgroundColor: '#f5f5f5', boxShadow: '2px 0 5px rgba(0, 0, 0, 0.1)' }}>
                {/* 自定义标点表单 */}
                <form onSubmit={handleAddCustomPoint} style={{ marginBottom: '20px' }}>
                    <h3 style={styles.heading}>添加自定义标点</h3>
                    <input
                        type="text"
                        placeholder="输入标点名称"
                        value={newPointName}
                        onChange={(e) => setNewPointName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <div style={{ marginTop: '10px' }}>
                        <span style={styles.label}>点击地图选择位置</span>
                        <div style={styles.locationDisplay}>
                            {newPointLocation.lat && newPointLocation.lng
                                ? `已选位置: ${newPointLocation.lat.toFixed(4)}, ${newPointLocation.lng.toFixed(4)}`
                                : '未选择位置'}
                        </div>
                    </div>
                    <button type="submit" style={styles.button}>
                        添加标点
                    </button>
                </form>

                {/* 清除选中 */}
                <button onClick={() => setSelectedRestaurants([])} style={styles.button}>
                    清除选中
                </button>

                {/* 展示所有标点 */}
                <RestaurantList
                    list={allPoints}
                    selectedLocations={selectedRestaurants}
                    onClickHandler={handleRestaurantClick}
                />

                <hr style={{ margin: '20px 0' }} />

                {/* 路线保存功能 */}
                <form onSubmit={handleSaveRoute} style={{ marginBottom: '20px' }}>
                    <h3 style={styles.heading}>保存路线</h3>
                    <input
                        type="text"
                        placeholder="输入路线名称"
                        value={newRouteName}
                        onChange={(e) => setNewRouteName(e.target.value)}
                        style={styles.input}
                        required
                    />
                    <button type="submit" style={styles.button}>
                        保存当前路线
                    </button>
                </form>

                {/* 展示保存的路线列表 */}
                <div>
                    <h4 style={styles.heading}>已保存的路线</h4>
                    <ul style={{ listStyle: 'none', padding: 0 }}>
                        {savedRoutes.map((routeObj, index) => (
                            <li
                                key={index}
                                style={{ padding: '5px 0', cursor: 'pointer', color: '#007bff' }}
                                onClick={() => setActiveRoute(routeObj.route)}
                            >
                                {routeObj.name}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>

            {/* 地图组件 */}
            <div style={{ flex: 1 }}>
                <Map
                    apikey={apikey}
                    userPosition={userPosition}
                    selectedLocations={selectedRestaurants}
                    onMapClick={handleMapClick}
                    customPoints={customPoints}
                    restaurantList={restaurantList}
                    activeRoute={activeRoute}  // 将当前选中的路线传递给 Map 组件
                />
            </div>
        </div>
    );
}

// CSS 样式
const styles = {
    button: {
        width: '100%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
        marginBottom: '20px',
        transition: 'background-color 0.3s ease',
    },
    input: {
        width: '100%',
        padding: '10px',
        marginBottom: '10px',
        border: '1px solid #ddd',
        borderRadius: '5px',
        fontSize: '16px',
    },
    heading: {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#333',
    },
    label: {
        fontSize: '14px',
        color: '#666',
    },
    locationDisplay: {
        padding: '10px',
        backgroundColor: '#fff',
        border: '1px solid #ddd',
        borderRadius: '5px',
        marginTop: '5px',
        fontSize: '14px',
        color: '#333',
    },
};

export default App;
