import React, { useEffect, useRef, useCallback } from 'react';
import H from '@here/maps-api-for-javascript';
import debounce from 'lodash.debounce';

const Map = (props) => {
    const mapRef = useRef(null);
    const map = useRef(null);
    const platform = useRef(null);
    const { apikey, userPosition, selectedLocations, onMapClick, customPoints, restaurantList, onRouteCalculated } = props;

    // 防抖包装的路线计算函数，延迟 1 秒后执行
    const debouncedCalculateRoute = useCallback(
        debounce((plat, mapObj, start, waypoints, callback) => {
            calculateRoute(plat, mapObj, start, waypoints, callback);
        }, 1000),
        []
    );

    useEffect(() => {
        if (!map.current) {
            // 初始化地图
            platform.current = new H.service.Platform({ apikey });
            const defaultLayers = platform.current.createDefaultLayers({ pois: true });
            const newMap = new H.Map(
                mapRef.current,
                defaultLayers.vector.normal.map,
                {
                    zoom: 14,
                    center: userPosition,
                }
            );

            newMap.addEventListener('tap', (evt) => {
                const coord = newMap.screenToGeo(evt.currentPointer.viewportX, evt.currentPointer.viewportY);
                console.log('Map clicked:', coord.lat, coord.lng);
                if (onMapClick) {
                    onMapClick(coord.lat, coord.lng);
                }
            });

            new H.mapevents.Behavior(new H.mapevents.MapEvents(newMap));
            H.ui.UI.createDefault(newMap, defaultLayers);
            map.current = newMap;
        }

        // 清除地图上所有对象
        map.current?.removeObjects(map.current.getObjects());

        // 添加用户位置标记
        map.current?.addObject(
            new H.map.Marker(userPosition, { icon: getMarkerIcon('red') })
        );

        // 添加预定义餐厅标记
        restaurantList.forEach(restaurant => {
            map.current?.addObject(
                new H.map.Marker(restaurant.location, { icon: getMarkerIcon('blue') })
            );
        });

        // 添加自定义点标记
        customPoints.forEach(point => {
            map.current?.addObject(
                new H.map.Marker(point.location, { icon: getMarkerIcon('purple') })
            );
        });

        // 如果有选中的点，则使用防抖后计算路线
        if (selectedLocations.length > 0) {
            debouncedCalculateRoute(platform.current, map.current, userPosition, selectedLocations, onRouteCalculated);
        }
    }, [apikey, userPosition, selectedLocations, onMapClick, customPoints, restaurantList, onRouteCalculated, debouncedCalculateRoute]);

    return <div style={{ width: '100%', height: '500px' }} ref={mapRef} />;
};

function getMarkerIcon(color) {
    const svgCircle = `<svg width="20" height="20" version="1.1" xmlns="http://www.w3.org/2000/svg">
                <g id="marker">
                <circle cx="10" cy="10" r="7" fill="${color}" stroke="${color}" stroke-width="4" />
                </g></svg>`;
    return new H.map.Icon(svgCircle, { anchor: { x: 10, y: 10 } });
}

function calculateRoute(platform, map, start, waypoints, onRouteCalculated) {
    const router = platform.getRoutingService(null, 8);
    const routingParams = {
        origin: `${start.lat},${start.lng}`,
        destination: `${waypoints[waypoints.length - 1].lat},${waypoints[waypoints.length - 1].lng}`,
        transportMode: 'pedestrian',
        return: 'polyline'
    };

    if (waypoints.length > 1) {
        routingParams.via = waypoints
            .slice(0, -1)
            .map(point => `${point.lat},${point.lng}`)
            .join('!');
    }

    router.calculateRoute(routingParams, (response) => {
        if (response.routes && response.routes.length > 0) {
            const sections = response.routes[0].sections;
            const lineStrings = sections.map(section =>
                H.geo.LineString.fromFlexiblePolyline(section.polyline)
            );
            const multiLineString = new H.geo.MultiLineString(lineStrings);
            const bounds = multiLineString.getBoundingBox();

            const routePolyline = new H.map.Polyline(multiLineString, {
                style: { lineWidth: 5, strokeColor: 'rgba(0, 128, 255, 0.7)' }
            });

            map.removeObjects(map.getObjects());
            map.addObject(routePolyline);

            const markers = [
                new H.map.Marker(start, { icon: getMarkerIcon('red') })
            ];
            waypoints.forEach((point, index) => {
                markers.push(
                    new H.map.Marker(point, {
                        icon: getMarkerIcon(index === waypoints.length - 1 ? 'green' : 'blue')
                    })
                );
            });
            map.addObjects(markers);
            map.getViewModel().setLookAtData({ bounds });

            if (typeof onRouteCalculated === 'function') {
                onRouteCalculated({
                    polyline: sections[0].polyline,
                    bounds: bounds,
                    start: start,
                    waypoints: waypoints
                });
            }
        } else {
            console.error('No routes found in the response:', response);
        }
    }, (error) => {
        console.error('Error calculating route:', error);
    });
}

export default Map;
